mapper.select:
SELECT
u.mobile,i.amount,ir.repay_date,ir.actual_repay_date,ir.expected_interest,ir.repay_amount,ir.status
FROM
invest_repay ir
JOIN
invest i ON ir.invest_id = i.id AND i.loan_id = 1
JOIN
user u ON i.login_name = u.login_name


        and u.mobile = #{mobile}


        and ir.repay_date >= #{startTime}


        and ir.repay_date <= #{endTime}


        and ir.status = #{repayStatus}


ORDER BY ir.repay_date
limit #{index},#{pageSize}

SELECT
count(*)
FROM
invest_repay ir
JOIN
invest i ON ir.invest_id = i.id AND i.loan_id = 1
JOIN
user u ON i.login_name = u.login_name


        and u.mobile = #{mobile}


        and ir.repay_date >= #{startTime}


        and ir.repay_date <= #{endTime}


        and ir.status = #{repayStatus}

SELECT
sum(ir.expected_interest)
FROM
invest_repay ir
JOIN
invest i ON ir.invest_id = i.id AND i.loan_id = 1
JOIN
user u ON i.login_name = u.login_name


        and u.mobile = #{mobile}


        and ir.repay_date >= #{startTime}


        and ir.repay_date <= #{endTime}


        and ir.status = #{repayStatus}

SELECT
sum(ir.repay_amount)
FROM
invest_repay ir
JOIN
invest i ON ir.invest_id = i.id AND i.loan_id = 1
JOIN
user u ON i.login_name = u.login_name


        and u.mobile = #{mobile}


        and ir.repay_date >= #{startTime}


        and ir.repay_date <= #{endTime}


        and ir.status = #{repayStatus}
