mapper.select:
SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumbnail, show_picture,
content, created_time, updated_time, deleted
FROM `aa`.`licaiquan_article` WHERE id = #{id}

mapper.update:
UPDATE `aa`.`licaiquan_article` SET title = #{title}, creator_login_name = #{creatorLoginName},
checker_login_name = #{checkerLoginName}, author = #{author}, section = #{section}, source = #{source},
carousel = #{carousel}, thumbnail = #{thumbnail}, show_picture = #{showPicture}, content = #{content},
created_time = #{createdTime}, updated_time = #{updatedTime}
WHERE id = #{id}

mapper.select:
SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumbnail, show_picture, content, created_time, updated_time, deleted FROM `aa`.`licaiquan_article` WHERE id >= #{index} AND deleted = 0
AND title LIKE CONCAT(CONCAT('%', #{title}), '%')
AND section = #{section}
ORDER BY id,created_time DESC LIMIT #{pageSize}
