mapper.select:
SELECT l.`name` AS loan_name, l.`periods` AS periods, n.investLoginName AS invest_login_name, n.investAmount AS invest_amount, n.investTime AS invest_time, n.referrerLoginName AS referrer_login_name, n.role_name AS role, n.level, n.rewardAmount AS reward_amount, n.status, n.rewardTime AS reward_time, n.investName AS invest_name, n.referrerName AS referrer_name FROM (SELECT temp.investLoginName, temp.investAmount, temp.investTime, temp.loanId, temp.rewardAmount, temp.rewardTime, temp.referrerLoginName, temp.role_name, temp.status, temp.investName, temp.referrerName, rr.`level` FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`amount` AS investAmount, i.`created_time` AS investTime, i.`loan_id` AS loanId, i.investName, irr.`amount` AS rewardAmount, irr.`created_time` AS rewardTime, irr.`referrer_login_name` AS referrerLoginName, irr.`referrer_role` as role_name, irr.`status`, irr.referrerName FROM (SELECT a.*, b.`user_name` AS referrerName FROM invest_referrer_reward a LEFT JOIN account b ON a.`referrer_login_name` = b.`login_name`
a.`referrer_login_name` = #{referrerLoginName}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
AND a.`referrer_role` = #{role}
AND a.`referrer_role` != 'MERCHANDISER'
) irr JOIN (SELECT m.*, n.`user_name` AS investName FROM invest m LEFT JOIN account n ON m.`login_name` = n.`login_name`
m.`login_name` = #{investLoginName}
AND m.`created_time` >= #{investStartTime}
AND m.`created_time` <= #{investEndTime}
AND m.`status` = 'SUCCESS') i ON irr.`invest_id` = i.`id`) temp ON rr.`login_name` = temp.investLoginName AND rr.`referrer_login_name` = temp.referrerLoginName
rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE') ORDER BY n.rewardTime DESC LIMIT #{startLimit},#{endLimit}

SELECT COUNT(*) FROM (SELECT temp.investLoginName, temp.investAmount, temp.investTime, temp.loanId, temp.rewardAmount, temp.rewardTime, temp.referrerLoginName, temp.role_name, temp.status, temp.investName, temp.referrerName, rr.`level` FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`amount` AS investAmount, i.`created_time` AS investTime, i.`loan_id` AS loanId, i.investName, irr.`amount` AS rewardAmount, irr.`created_time` AS rewardTime, irr.`referrer_login_name` AS referrerLoginName, irr.`referrer_role` as role_name, irr.`status`, irr.referrerName FROM (SELECT a.*, b.`user_name` AS referrerName FROM invest_referrer_reward a LEFT JOIN account b ON a.`referrer_login_name` = b.`login_name`
a.`referrer_login_name` = #{referrerLoginName}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
AND a.`referrer_role` = #{role}
AND a.`referrer_role` != 'MERCHANDISER'
) irr JOIN (SELECT m.*, n.`user_name` AS investName FROM invest m LEFT JOIN account n ON m.`login_name` = n.`login_name`
m.`login_name` = #{investLoginName}
AND m.`created_time` >= #{investStartTime}
AND m.`created_time` <= #{investEndTime}
AND m.`status` = 'SUCCESS') i ON irr.`invest_id` = i.`id`) temp ON rr.`login_name` = temp.investLoginName AND rr.`referrer_login_name` = temp.referrerLoginName
rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE') ORDER BY n.rewardTime DESC

SELECT
    rr.login_name,
    rr.level,
    u.register_time
FROM
    referrer_relation rr
LEFT JOIN user u
    on rr.login_name = u.login_name


         rr.level <= #{level} 


        and rr.referrer_login_name = #{referrerLoginName}


         and u.`register_time` >= #{referStartTime} 


         and u.`register_time` <= #{referEndTime} 


ORDER BY u.register_time DESC limit #{startLimit}, #{endLimit}

SELECT
    count(rr.referrer_login_name)
FROM
    referrer_relation rr
LEFT JOIN user u
    on rr.login_name = u.login_name


         rr.level <= #{level} 


        and rr.referrer_login_name = #{referrerLoginName}


         and u.`register_time` >= #{referStartTime} 


         and u.`register_time` <= #{referEndTime}

select
    rr.login_name AS invest_name,
    rr.level,
    l.name AS loan_name,
    i.amount AS invest_amount,
    l.periods,
    i.created_time AS invest_time,
    irr.amount AS reward_amount,
    irr.created_time AS reward_time
from
    invest i
right join referrer_relation rr
    on i.login_name = rr.login_name
    and rr.referrer_login_name = #{referrerLoginName}

        and rr.login_name=#{loginName}

left join loan l
    on l.id = i.loan_id
    left join invest_referrer_reward irr
    on irr.invest_id=i.id and irr.referrer_login_name = #{referrerLoginName}


         rr.level <= #{level} 


         and i.`created_time` >= #{investStartTime} 


         and i.`created_time` <= #{investEndTime} 


ORDER BY invest_time DESC limit #{startLimit}, #{endLimit}

select
    COUNT(i.id)
from
    invest i
right join referrer_relation rr
    on i.login_name = rr.login_name
    and rr.referrer_login_name = #{referrerLoginName}

        and rr.login_name=#{loginName}

left join loan l
    on l.id = i.loan_id
    left join invest_referrer_reward irr
    on irr.invest_id=i.id and irr.referrer_login_name = #{referrerLoginName}


             rr.level <= #{level} 


             and i.`created_time` >= #{investStartTime} 


             and i.`created_time` <= #{investEndTime}
